import React, { useState } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  List,
  ListItem,
  ListItemText
} from '@mui/material';
import config from '../config';

function URLStats() {
  const [shortcode, setShortcode] = useState('');
  const [stats, setStats] = useState(null);

  const fetchStats = async () => {
    try {
      const res = await fetch(`${config.baseURL}/shorturls/${shortcode}`, {
        headers: {
          clientid: config.clientID,
          clientsecret: config.clientSecret,
        },
      });
      const data = await res.json();
      setStats(data);
    } catch (err) {
      console.error('Error fetching stats:', err);
    }
  };

  return (
    <Paper elevation={3} sx={{ p: 3 }}>
      <Typography variant="h6" gutterBottom>
        Fetch Short URL Stats
      </Typography>

      <Box display="flex" gap={2} mb={2}>
        <TextField
          fullWidth
          label="Shortcode"
          value={shortcode}
          onChange={(e) => setShortcode(e.target.value)}
        />
        <Button variant="contained" onClick={fetchStats}>
          Get Stats
        </Button>
      </Box>

      {stats && (
        <Box>
          <Typography><strong>Original URL:</strong> {stats.url}</Typography>
          <Typography><strong>Created At:</strong> {new Date(stats.createdAt).toLocaleString()}</Typography>
          <Typography><strong>Expires At:</strong> {new Date(stats.expiry).toLocaleString()}</Typography>
          <Typography><strong>Total Clicks:</strong> {stats.clicks}</Typography>

          <Typography mt={2}><strong>Click Details:</strong></Typography>
          <List>
            {stats.clickStats.map((click, index) => (
              <ListItem key={index}>
                <ListItemText
                  primary={`Time: ${new Date(click.timestamp).toLocaleString()}`}
                  secondary={`IP: ${click.ip} | Referer: ${click.referer}`}
                />
              </ListItem>
            ))}
          </List>
        </Box>
      )}
    </Paper>
  );
}

export default URLStats;
