import React, { useState } from 'react';
import config from '../config';

function URLForm({ onSuccess }) {
  const [urls, setUrls] = useState([{ url: '', shortcode: '', validity: '' }]);

  const handleChange = (i, field, value) => {
    const newUrls = [...urls];
    newUrls[i][field] = value;
    setUrls(newUrls);
  };

  const addField = () => {
    if (urls.length < 5) {
      setUrls([...urls, { url: '', shortcode: '', validity: '' }]);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    for (let item of urls) {
      if (!item.url) continue;

      try {
        const res = await fetch(`${config.baseURL}/shorturls`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            clientid: config.clientID,
            clientsecret: config.clientSecret,
          },
          body: JSON.stringify({
            url: item.url,
            shortcode: item.shortcode || undefined,
            validity: parseInt(item.validity) || undefined,
          }),
        });

        const data = await res.json();
        onSuccess(data); // Send to parent to display results
      } catch (err) {
        console.error('Error shortening URL:', err);
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Shorten URLs</h2>
      {urls.map((item, i) => (
        <div key={i}>
          <input
            type="text"
            placeholder="Original URL"
            value={item.url}
            onChange={(e) => handleChange(i, 'url', e.target.value)}
          />
          <input
            type="text"
            placeholder="Custom Shortcode (optional)"
            value={item.shortcode}
            onChange={(e) => handleChange(i, 'shortcode', e.target.value)}
          />
          <input
            type="number"
            placeholder="Validity in minutes (optional)"
            value={item.validity}
            onChange={(e) => handleChange(i, 'validity', e.target.value)}
          />
        </div>
      ))}
      <button type="button" onClick={addField}>+ Add More</button>
      <button type="submit">Shorten</button>
    </form>
  );
}

export default URLForm;
